JSEncrypt = require("jsencrypt")

function encrypt(pwd){
	var key = "";
	var encrypt = new JSEncrypt();
    encrypt.setPublicKey(key);
    var encrypted = encrypt.encrypt(pwd);
    return encrypted;
}

